<style lang="scss">
	header {
		padding-top: max(env(safe-area-inset-top), 12px);
	}

	.switch {
		position: relative;
		display: inline-block;
		width: 60px;
		height: 34px;
	}

	/* Hide default HTML checkbox */
	.switch input {
		opacity: 0;
		width: 0;
		height: 0;
	}

	/* The slider */
	.slider {
		position: absolute;
		cursor: pointer;
		top: 0;
		left: 0;
		right: 0;
		bottom: 0;
		background-color: #fff;
		transition: 0.2s;
		border: 1px solid theme('colors.brown');
		box-shadow: inset -1px -1px 2px rgba(143, 57, 0, 0.54);
		&:before {
			border: 1px solid theme('colors.brown');
			position: absolute;
			content: '';
			height: 34px;
			width: 34px;
			left: -1px;
			bottom: -1px;
			background-color: theme('colors.orange');
			transition: 0.2s;
			box-shadow:
